
public class Datentype01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte a;
		byte num = 127;
		byte y = 5;
		byte ergeb;
		a = (byte) 256;
		System.out.println("Das Ergebnis in Byte für (256) = " + a);
		a = (byte) 128;
		System.out.println("Das Ergebnis in Byte für (128) = " + a);
		a = (byte) 255;
		System.out.println("Das Ergebnis in Byte für (255) = " + a);
		a = (byte) 200;
		System.out.println("Das Ergebnis in Byte für (255) = " + a);
		ergeb = (byte) (num + y);
		System.out.println("Das Ergebnis in Byte für (Ergeb) = " + ergeb);

		System.out.println("The program is completed");

	}

}
